package com.wangchao.shares.service;import java.io.Serializable;import com.wangchao.shares.util.BoundListRedisSupport;import com.wangchao.shares.vo.WaybillBufferVo;import org.apache.commons.lang.StringUtils;import org.springframework.data.redis.core.RedisTemplate;import org.springframework.data.redis.serializer.StringRedisSerializer;import com.alibaba.fastjson.JSON;import org.springframework.stereotype.Service;@Servicepublic class WaybillCodeQueue extends BoundListRedisSupport<String> {	private String preKey = "waybill_code_queue";	private String billCodeType;	public WaybillCodeQueue() {			}		public WaybillCodeQueue(String billCodeType) {		super();		this.billCodeType = billCodeType;		this.key = preKey + "_" + this.billCodeType + ":List";	}	public void setRedisTemplate(RedisTemplate<Serializable, String> redisTemplate) {		this.redisTemplate = redisTemplate;		this.redisTemplate.setValueSerializer(new StringRedisSerializer());		boundListOperations = this.redisTemplate.boundListOps(key); 	}		public WaybillBufferVo getOneWaybillBufferPop() {		String waybillBufferStr = super.pop();		if(StringUtils.isNotBlank(waybillBufferStr)) {			WaybillBufferVo bufferDto = JSON.parseObject(waybillBufferStr,WaybillBufferVo.class);			return bufferDto;		}		return null;	}	public String getBillCodeType() {		return billCodeType;	}	public void setBillCodeType(String billCodeType) {		this.billCodeType = billCodeType;	}     }