mapper.select:
select share_code shareCode, share_name shareName, new_price newPrice, amount_increase amountIncrease, zhuli_liuru_jinge zhuliLiuruJinge, zhuli_liuru_baifenbi zhuliLiuruBaifenbi, zhuli_bigdan_liuru_jinge zhuliBigdanLiuruJinge, zhuli_bigdan_liuru_zhanbi zhuliBigdanLiuruZhanbi, zhuliliuru_jinge zhuliliuruJinge, zhuliliuru_zhanbi zhuliliuruZhanbi, zhuli_middle_liuru_jinge zhuliMiddleLiuruJinge, zhuli_middle_liuru_zhanbi zhuliMiddleLiuruZhanbi, zhuli_small_liuru_jinge zhuliSmallLiuruJinge, zhuli_small_liuru_zhanbi zhuliSmallLiuruZhanbi FROM shares_info
count_data=#{countData}
and share_code=#{shareCode}
and share_code LIKE CONCAT(CONCAT(#{type},'%'))
and share_name LIKE CONCAT(CONCAT('%',#{shareName},'%'))
and new_price BETWEEN #{minPrice} and #{maxPrice}
and amount_increase BETWEEN #{minAmountIncrease} and #{maxAmountIncrease}
order by new_price desc
